insert into articles_description (articles_id, language_id, articles_name, articles_description, articles_head_desc_tag, articles_url, articles_viewed) values ('1', '1', 'About Us', '<p style=\"text-align: justify;\"><span style=\"font-size: x-small;\"><span style=\"font-family: verdana,geneva;\">The Corner&rsquo;s Mall&trade; is a large virtual complex containing a variety of retail stores offering more than 10,000 lines of branded products such as women\'s clothing, man&rsquo;s clothing, </span><span style=\"font-family: verdana,geneva;\">shoes, accessories, jewelry, electronics, tourism, </span><span style=\"font-family: verdana,geneva;\">health and beauty; with the ability to send its products to over 190 countries from its virtual corporate headquarters located </span><span style=\"font-family: verdana,geneva;\">in the State of Pennsylvania, </span><span style=\"font-family: verdana,geneva;\">USA.</span></span><br /><br /><span style=\"font-size: x-small;\"><span style=\"font-family: verdana,geneva;\">The Corner&rsquo;s Mall belongs to The LABIAH<sup>&reg;</sup>Group (Latin-American Business Adventure Hub), a private organization that represents a diverse group of Latin-American investors and </span><span style=\"font-family: verdana,geneva;\">consultants.</span></span></p>
