insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('284', '1', 'Men\'s Walk On Air Slippers', '<p style=\"text-align: justify;\"><span style=\"font-family: verdana,geneva; font-size: xx-small;\"><a href=\"http://www.skymall.com/shopping/detail.htm?pid=204075142&amp;pnr=W22&amp;cm_mmc=ls-_-affiliate-_-Catalog-_-204075142&amp;pnr=W22&amp;siteID=pZ7cUp4bUz4-fyTQx.GBAh3unztlrpS0kQ&amp;cm_mmc=ls-_-SkyMall-_-pZ7cUp4bUz4-_-banner\">Only available at Hammacher Schlemmer, these are the indoor / outdoor slippers that have over 1,000 air bubbles injected into the outsoles and insoles, allowing the feet to rest on a cushion of air. Following the recommendations of leading German podiatrists, the slippers have lightweight polyurethane soles and anatomically molded footbeds that cradle your feet, ensuring optimal support while lessening foot fatigue. Tanned in Germany by artisans with over 200 years of experience, each cowhide selected for the soft, supple nubuck leather uppers is hand-picked to identify the highest quality available. A breathable microfiber lining absorbs moisture and passes it to the outside of the slipper, keeping your feet dry. The non-skid sole ensures stable footing.</a></span></p>', 'www.skymall.com/shopping/detail.htm?pid=204075142&pnr=W22&cm_mmc=ls-_-affiliate-_-Catalog-_-204075142&pnr=W22&siteID=pZ7cUp4bUz4-fyTQx.GBAh3unztlrpS0kQ&cm_mmc=ls-_-SkyMall-_-pZ7cUp4bUz4-_-banner', '13');
