insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('285', '1', 'Propet Men\'s Scotchgard Shoe', '<p style=\"text-align: justify;\"><span style=\"font-family: verdana,geneva; font-size: xx-small;\"><a href=\"http://www.skymall.com/shopping/detail.htm?pid=204634710&amp;pnr=W22&amp;cm_mmc=ls-_-affiliate-_-Catalog-_-204634710&amp;pnr=W22&amp;siteID=pZ7cUp4bUz4-xFDAH0GDUQDpReKaWAe0.Q&amp;cm_mmc=ls-_-SkyMall-_-pZ7cUp4bUz4-_-banner\">Propet Men\'s Scotchgard Leather Walker Slip-On Shoes -- these washable walkers know how to stay clean! Adjust the washing machine to gentle and toss in this washable suede shoe to keep it clean and presentable. Plus, Scotchgard treatment resists staining. All the details you\'d expect in a quality active shoe are here: excellent heel and arch support, molded EVA midsole for shock absorption, removable, breathable insole, stretchable, padded nylon / neoprene collars and durable rubber outsole. A heel tab makes on and off so much easier. Backed by the world-famous Propet 1,000 mile guarantee.</a></span></p>', 'www.skymall.com/shopping/detail.htm?pid=204634710&pnr=W22&cm_mmc=ls-_-affiliate-_-Catalog-_-204634710&pnr=W22&siteID=pZ7cUp4bUz4-xFDAH0GDUQDpReKaWAe0.Q&cm_mmc=ls-_-SkyMall-_-pZ7cUp4bUz4-_-banner', '11');
