insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('286', '1', 'Sheepskin Slippers', '<p style=\"text-align: justify;\"><span style=\"font-family: verdana,geneva; font-size: xx-small;\"><a href=\"http://www.skymall.com/shopping/detail.htm?pid=102718135&amp;pnr=W22&amp;cm_mmc=ls-_-affiliate-_-Catalog-_-102718135&amp;pnr=W22&amp;siteID=pZ7cUp4bUz4-Q0OZMJuy2JbDIsiApge0eA&amp;cm_mmc=ls-_-SkyMall-_-pZ7cUp4bUz4-_-banner\">These comfortably soft slippers have adjustable uppers made from Mongolian sheepskin that provide a custom fit for narrow or wide feet. The double-walled uppers are made from durable chestnut napped suede and are adjusted using hook-and-loop fasteners. The sheepskin interiors\' dense, hollow fibers allow air to circulate, keeping your feet toasty warm. The slippers have a roomy toe area and cushioned insole is made from sheepskin for maximum comfort. The rippled rubber outsole has a raised high-grip herringbone design that ensures stable footing indoors and outdoors. (1-1/4 lbs.) Men\'s. Sizes S (7-1/2 to 8-1/2), M (9-10), L (10-1/2 to 11-1/2), XL (12-13), or XXL (14-15).</a></span></p>', 'www.skymall.com/shopping/detail.htm?pid=102718135&pnr=W22&cm_mmc=ls-_-affiliate-_-Catalog-_-102718135&pnr=W22&siteID=pZ7cUp4bUz4-Q0OZMJuy2JbDIsiApge0eA&cm_mmc=ls-_-SkyMall-_-pZ7cUp4bUz4-_-banner', '14');
