insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('290', '1', 'Pink Sugar Sensual by Aquolina', '<p style=\"text-align: justify;\"><span style=\"font-family: verdana,geneva; font-size: xx-small;\"><a href=\"http://www.skymall.com/shopping/detail.htm?pid=204096320&amp;pnr=W22&amp;cm_mmc=ls-_-affiliate-_-Catalog-_-204096320&amp;pnr=W22&amp;siteID=pZ7cUp4bUz4-vNapeNM_KUnJppX2SqmyZA&amp;cm_mmc=ls-_-SkyMall-_-pZ7cUp4bUz4-_-banner\">A floral fruity fragrance for modern women. Fresh, sparkling, sweet &amp; alluring. Packed in a pink flacon decorated with seductive black lace. Top notes of the fragrance introduce sweet-tart sparks of black currant, tangerine and bergamot, as an fresh-fruity overture to a heart composed of orange blossom, light nuances of pink jasmine petals and tiare flowers. Base notes seduce with sweetness of vanilla, black sugar and woody accords of sandalwood. 3.4 oz Eau de Toilette Spray. Includes Free Deluxe Gift: Pink Sugar Body Mousse 1.7 oz</a></span></p>', 'www.skymall.com/shopping/detail.htm?pid=204096320&pnr=W22&cm_mmc=ls-_-affiliate-_-Catalog-_-204096320&pnr=W22&siteID=pZ7cUp4bUz4-vNapeNM_KUnJppX2SqmyZA&cm_mmc=ls-_-SkyMall-_-pZ7cUp4bUz4-_-banner', '17');
