insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('291', '1', 'Original Penguin for Him', '<p style=\"text-align: justify;\"><span style=\"font-family: verdana,geneva; font-size: xx-small;\"><a href=\"http://www.skymall.com/shopping/detail.htm?pid=204096319&amp;pnr=W22&amp;cm_mmc=ls-_-affiliate-_-Catalog-_-204096319&amp;pnr=W22&amp;siteID=pZ7cUp4bUz4-VGkMr8R8e9mZGl30ARahUA&amp;cm_mmc=ls-_-SkyMall-_-pZ7cUp4bUz4-_-banner\">Original Penguin may have made its mark in the 1950s, but it continues to live on today. This fresh, contemporary oriental fragrance is the perfect blend of heritage, wit and innovation. The fragrance opens with fresh, captivating hints of Mediterranean neroli and golden apple. 3.4 oz Eau de Toilette Spray. Includes free deluxe laptop bag.</a></span></p>', 'www.skymall.com/shopping/detail.htm?pid=204096319&pnr=W22&cm_mmc=ls-_-affiliate-_-Catalog-_-204096319&pnr=W22&siteID=pZ7cUp4bUz4-VGkMr8R8e9mZGl30ARahUA&cm_mmc=ls-_-SkyMall-_-pZ7cUp4bUz4-_-banner', '14');
