insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('293', '1', 'Forbidden Flower by Lolita Lempicka', '<p style=\"text-align: justify;\"><span style=\"font-family: verdana,geneva; font-size: xx-small;\"><a href=\"http://www.skymall.com/shopping/detail.htm?pid=204096317&amp;pnr=W22&amp;cm_mmc=ls-_-affiliate-_-Catalog-_-204096317&amp;pnr=W22&amp;siteID=pZ7cUp4bUz4-.fiRn.CcN5pSot55XngsXQ&amp;cm_mmc=ls-_-SkyMall-_-pZ7cUp4bUz4-_-banner\">Inspired by the innocent and intoxicating scent of Absinth Flower. It is a swirl of the organic, tonic and slightly sharp notes of Strawberry Leaf and Mimosa Stem. Its heart is peppered with Peony, that shines with radiant sensuality, the rich powdery scent of Violet and the forbidden taste of Aniseed Flower, just for her. 3.4 oz Eau de Parfum Spray. Includes Free Deluxe Gift: St Valentin EDP Premier Mini Perfume 5 ml</a></span></p>', 'www.skymall.com/shopping/detail.htm?pid=204096317&pnr=W22&cm_mmc=ls-_-affiliate-_-Catalog-_-204096317&pnr=W22&siteID=pZ7cUp4bUz4-.fiRn.CcN5pSot55XngsXQ&cm_mmc=ls-_-SkyMall-_-pZ7cUp4bUz4-_-banner', '16');
