insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('295', '1', 'Ombre Rose for Women by Jean-Charles Brosseau', '<p style=\"text-align: justify;\"><span style=\"font-family: verdana,geneva; font-size: xx-small;\"><a href=\"http://www.skymall.com/shopping/detail.htm?pid=204096329&amp;pnr=W22&amp;cm_mmc=ls-_-affiliate-_-Catalog-_-204096329&amp;pnr=W22&amp;siteID=pZ7cUp4bUz4-I1rwy183u5l3nZfhbB0ymQ&amp;cm_mmc=ls-_-SkyMall-_-pZ7cUp4bUz4-_-banner\">Ombre Rose L\'Original by Jean Charles Brosseau is a Floral fragrance for women. Ombre Rose L\'Original was launched in 1981. The nose behind this fragrance is Francoise Caron. Top notes are aldehydes, peach, Brazilian rosewood and geranium; middle notes are sandalwood, orris root, vetiver, ylang-ylang, lily-of-the-valley, cedar and rose; base notes are honey, orris, tonka bean, cinnamon, musk, vanilla and heliotrope. 3.4 oz Eau de Toilette Spray. Includes Free Deluxe Gift: Ombre Rose Miniaute L\'Original</a></span></p>', 'www.skymall.com/shopping/detail.htm?pid=204096329&pnr=W22&cm_mmc=ls-_-affiliate-_-Catalog-_-204096329&pnr=W22&siteID=pZ7cUp4bUz4-I1rwy183u5l3nZfhbB0ymQ&cm_mmc=ls-_-SkyMall-_-pZ7cUp4bUz4-_-banner', '12');
