insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('296', '1', 'ITISLOVE by Salvador Dali', '<p style=\"text-align: justify;\"><span style=\"font-family: verdana,geneva; font-size: xx-small;\"><a href=\"http://www.skymall.com/shopping/detail.htm?pid=204096328&amp;pnr=W22&amp;cm_mmc=ls-_-affiliate-_-Catalog-_-204096328&amp;pnr=W22&amp;siteID=pZ7cUp4bUz4-W8v8CKrbzNKglqhEq_.eow&amp;cm_mmc=ls-_-SkyMall-_-pZ7cUp4bUz4-_-banner\">ITISLOVE is an ultra sensual fragrance for women that draws you into a world of feverish and passionate nights where the heart beats wildly and the body delightfully succumbs to the most extravagant temptations. 3.4 oz Eau de Toilette Intense Spray. Includes Free Deluxe Gift: Salvador Dali ITISLOVE Body Lotion</a></span></p>', 'www.skymall.com/shopping/detail.htm?pid=204096328&pnr=W22&cm_mmc=ls-_-affiliate-_-Catalog-_-204096328&pnr=W22&siteID=pZ7cUp4bUz4-W8v8CKrbzNKglqhEq_.eow&cm_mmc=ls-_-SkyMall-_-pZ7cUp4bUz4-_-banner', '18');
