insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('711', '1', 'AT Paddles River Carbon Whitewater Kayak Paddle-191cm', '<p style=\"text-align: justify;\"><span style=\"font-family: verdana,geneva;\"><a href=\"http://www.outdoorplay.com/AT-River-Carbon-Kayak-Paddle?utm_medium=affiliate&amp;utm_source=commissionjunction&amp;cvsfa=3650&amp;cvsfe=22&amp;cvsfhu=36373034\">AT Paddles River Carbon Whitewater Kayak Paddle - The AT River Carbon kayak paddle combines Adventure Technology\'s patented ergonomic bent shaft with the bombproof durability of a thermoplastic nylon matrix. This nylon resin bonds with the long fiber fiberglass weave to yield the highest shaft strength possible. Ideal for down-river paddling, the patented Full Control Grip ergonomic shaft is shaped three-dimensionally, allowing proper alignment of the wrists and relieving major stress on tendons and ligaments. You\'ll get unparalleled blade control and extra assurance for rolling and bracing. The stable dihedral blades are ideal for all-around use. The large surface area maximizes each stroke and maintains flutter-free performance. Seasoned paddlers will appreciate the versatility of a paddle they can use to creek, surf or run big water. Novices will experience increased confidence due</a></span></p>', 'www.outdoorplay.com/AT-River-Carbon-Kayak-Paddle?utm_medium=affiliate&utm_source=commissionjunction&cvsfa=3650&cvsfe=22&cvsfhu=36373034', '17');
