insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('713', '1', 'Werner Sherpa Fiberglass Whitewater Kayak Paddle-SS-197cm', '<p style=\"text-align: justify;\"><span style=\"font-family: verdana,geneva; font-size: xx-small;\"><a href=\"http://www.outdoorplay.com/Werner-Sherpa-Fiberglass-Premium-Kayak-Paddle?utm_medium=affiliate&amp;utm_source=commissionjunction&amp;cvsfa=3650&amp;cvsfe=22&amp;cvsfhu=38373739\">Werner Sherpa Fiberglass Kayak Paddle - The Sherpa Fiberglass Paddle by Werner is an exceptionally smooth and responsive river running design. The mid-size blades are evenly balanced to create a clean bite on the water that is powerful and smooth from catch to release. The slight dihedral offers the right combination of maneuvering without losing power. Constructed using the highest quality continuous weave fiberglass materials with superior strength and durability, the Sherpa kayak paddle excels in strength and is rightfully known as bomber! The one-piece shaft is available in either straight or neutral bent shaft versions. If you like running rivers and ripping up a wave or two, the Werner Sherpa premium fiberglass kayak paddle is the choice for you.</a></span></p>', 'www.outdoorplay.com/Werner-Sherpa-Fiberglass-Premium-Kayak-Paddle?utm_medium=affiliate&utm_source=commissionjunction&cvsfa=3650&cvsfe=22&cvsfhu=38373739', '14');
