insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('714', '1', 'Shred Ready Super Scrappy Whitewater Kayak Helmet', '<p style=\"text-align: justify;\"><span style=\"font-family: verdana,geneva; font-size: xx-small;\"><a href=\"http://www.outdoorplay.com/Shred-Ready-Super-Scrappy-Kayak-Helmet?utm_medium=affiliate&amp;utm_source=commissionjunction&amp;cvsfa=3650&amp;cvsfe=22&amp;cvsfhu=31383735\">Shred Ready Super Scrappy Whitewater Kayak Helmet - The Shred Ready Super Scrappy helmet is the perfect helmet for kayaking. The ABS injection-molded outer shell and multi-impact liner help protect your skull. An adjustable retention system lets you wear the bill to the front or rear. The H.O.G 2.0 Occipital Lock fits behind nape of your neck for a secure custom fit. Plush interchangeable fit pads lets you really dial your sizing in. Removable ear pads even let you adjust for weather and conditions. Shred Ready\'s got you covered with the Super Scrappy helmet.</a></span></p>', 'www.outdoorplay.com/Shred-Ready-Super-Scrappy-Kayak-Helmet?utm_medium=affiliate&utm_source=commissionjunction&cvsfa=3650&cvsfe=22&cvsfhu=31383735', '17');
