insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('716', '1', 'WRSI Current Kayak Helmet-Red-M/L', '<p style=\"text-align: justify;\"><span style=\"font-family: verdana,geneva; font-size: xx-small;\"><a href=\"http://www.outdoorplay.com/WRSI-Current-Kayak-Helmet?utm_medium=affiliate&amp;utm_source=commissionjunction&amp;cvsfa=3650&amp;cvsfe=22&amp;cvsfhu=31373732\">WRSI Current Kayak Helmet - The Current helmet is on the leading edge of development. The result of intense research work by WRSI and Johns Hopkins, it is a superior multi-impact helmet for use in a water environment. It features a four-layer multiple impact shell design for maximum absorption and dispersion of collisions. The O Brace retention system locks the helmet firmly in place on your head, preventing the helmet from rolling back and exposing the forehead. Special attention was paid to the chinstrap as well, resulting in a one-piece continuous strap that decreases rotation. Designed to fit a wide range of heads, the included pad system lets you customize the fit to further reduce movement and increase comfort. Ventilation holes allow air flow and drainage. Whether you\'re kayaking, rafting, sailing, or kiting, with the Current you\'ll be able to focus on having fun on the water.</a></span></p>', 'www.outdoorplay.com/WRSI-Current-Kayak-Helmet?utm_medium=affiliate&utm_source=commissionjunction&cvsfa=3650&cvsfe=22&cvsfhu=31373732', '18');
