insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('730', '1', 'Vegas Strip Night Helicopter Tour', '<p style=\"text-align: justify;\"><span style=\"font-family: verdana,geneva; font-size: xx-small;\"><a href=\"http://www.cloud9living.com/las-vegas/vegas-strip-night-helicopter-tour\">This is the ultimate Las Vegas Strip tour, where the excitement begins with a glass of champagne before you board your luxurious A-Star jet helicopter.As you rise above the City of Lights, you will see spectacular views of the dazzling Las Vegas Strip from the comfort of your jet helicopter.Cruise past famous landmarks such as:Caesar\'s PalaceMandalay BayExcaliburMGM GrandNew York, New YorkPlanet HollywoodBellagioThe VenetianWynnThe massive Stratosphere tower rising 1,149 feet into the airFremont StreetHistoric downtown Vegas and more!A Vegas experience not to miss!</a></span></p>', 'www.cloud9living.com/las-vegas/vegas-strip-night-helicopter-tour', '31');
