insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('822', '1', 'Slimming Full Bodysuit Shaper', '<p style=\"text-align: justify;\"><span style=\"font-family: verdana,geneva; font-size: xx-small;\"><a href=\"http://www.leonisa.com/en/shapewear/slimming-full-bodysuit-shaper/?utm_content=18687864&amp;utm_source=linkshare&amp;utm_medium=referral&amp;utm_campaign=pZ7cUp4bUz4&amp;siteID=pZ7cUp4bUz4-7wX7PSxdvm.Nn7izlFxEyQ&amp;ls_affid=pZ7cUp4bUz4\">Step into slimming splendor! Full body minimizing support to make you feel confident. It comfortably adjusts to your body to recuperate your figure after surgery. Triangle, contour cups support your bust. Control of the entire abdomen, waist, back and thighs allows your body to bounce back quickly. Easy to put on and take off with its front zipper with three internal hooks. Complete your treatment with the highest quality shapewear.</a></span></p>', 'http://www.leonisa.com/en/shapewear/slimming-full-bodysuit-shaper/?utm_content=18687864&utm_source=linkshare&utm_medium=referral&utm_campaign=pZ7cUp4bUz4&siteID=pZ7cUp4bUz4-7wX7PSxdvm.Nn7izlFxEyQ&ls_affid=pZ7cUp4bUz4', '18');
