insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('823', '1', 'Righteous Curves High Waist Tummy Shaper', '<p style=\"text-align: justify;\"><span style=\"font-family: verdana,geneva; font-size: xx-small;\"><a href=\"http://www.leonisa.com/en/shapewear/righteous-curves-high-waist-tummy-shaper/?utm_content=12811802&amp;utm_source=linkshare&amp;utm_medium=referral&amp;utm_campaign=pZ7cUp4bUz4&amp;siteID=pZ7cUp4bUz4-H7RtsUVlqICWU88VnGv48g&amp;ls_affid=pZ7cUp4bUz4\">Firm control panty shaper that controls the entire abdomen, waist, and back. It has non-slip elastic that helps keep the shaper in place. A new style: classic combined with light sheer fabric on the rear that makes it look like a thong. Enjoy the new invisible styles!</a></span></p>', 'www.leonisa.com/en/shapewear/righteous-curves-high-waist-tummy-shaper/?utm_content=12811802&utm_source=linkshare&utm_medium=referral&utm_campaign=pZ7cUp4bUz4&siteID=pZ7cUp4bUz4-H7RtsUVlqICWU88VnGv48g&ls_affid=pZ7cUp4bUz4', '21');
