insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('826', '1', 'Slimming Waist Cincher', '<p style=\"text-align: justify;\"><span style=\"font-family: verdana,geneva; font-size: xx-small;\"><a href=\"http://www.leonisa.com/en/products/slimming-waist-cincher-shaper/?utm_content=18624802&amp;utm_source=linkshare&amp;utm_medium=referral&amp;utm_campaign=pZ7cUp4bUz4&amp;siteID=pZ7cUp4bUz4-byLJBy3CV0i_tmdKzePLFA&amp;ls_affid=pZ7cUp4bUz4\">A quick-fix solution for a fabulous waistline! Most women\'s biggest concern is their tummy. This waist cincher slims your waist, tummy and back in a matter of seconds. Wear with your favorite bra. Made of 2 layers: powernet that offers reduction and a smooth elastic fabric that provides freshness and softness to the skin. It has front hooks with two positions to adjust to the level of compression you desire. Soft internal boning keeps it firmly in its place with no roll up.</a></span></p>', 'www.leonisa.com/en/products/slimming-waist-cincher-shaper/?utm_content=18624802&utm_source=linkshare&utm_medium=referral&utm_campaign=pZ7cUp4bUz4&siteID=pZ7cUp4bUz4-byLJBy3CV0i_tmdKzePLFA&ls_affid=pZ7cUp4bUz4', '21');
