insert into products_description (products_id, language_id, products_name, products_description, products_url, products_viewed) values ('884', '1', 'New Balance WO1520 Multi-Sport Brown Women\'s', '<p style=\"text-align: justify;\"><span style=\"font-family: verdana,geneva; font-size: xx-small;\"><a href=\"http://www.planetshoes.com/item/new-balance-wo1520-multi-sport/5346/157?source=ls&amp;utm_source=LINKSHARE&amp;utm_medium=affiliate&amp;utm_term=New+Balance+WO1520+Multi-Sport_5346-157_Women&amp;zmam=25971251&amp;zmas=1&amp;zmac=60&amp;zmap=5346-157&amp;siteID=pZ7cUp4bUz4-hrSeRtbrrp3dusABJAibLA\">Expand your outdoor playground in the New Balance WO1520 Multi-Sport. This sleek multi-sport women\'s shoe features a suede and mesh upper for durable, breathable protection. The C-CAP compression-molded EVA midsole plus Abzorb cushioning enhances comfort and support, while the Stability Web system provides arch support for this New Balance outdoor shoe. The NLock integrated lacing and webbing system secures a comfortable fit. Gore-Tex Extended Comfort Footwear offers advanced waterproof protection and durability with optimized climate control. The Vibram outsole provides maximum durability and multi-directional traction. Hiking, biking, trail running, whatever you\'re into, the New Balance WO1520 Multi-Sport will keep you safe and comfortable along the way!</a></span></p>', 'www.planetshoes.com/item/new-balance-wo1520-multi-sport/5346/157?source=ls&utm_source=LINKSHARE&utm_medium=affiliate&utm_term=New+Balance+WO1520+Multi-Sport_5346-157_Women&zmam=25971251&zmas=1&zmac=60&zmap=5346-157&siteID=pZ7cUp4bUz4-hrSeRtbrrp3dusABJAibL', '28');
